<!doctype html>
    <html lang=\"en\">
    <head>
        <meta name=\"viewport\"
              content=\"width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0\">
        <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\">


        <link rel=\"preconnect\" href=\"https://fonts.googleapis.com\">
        <link rel=\"preconnect\" href=\"https://fonts.gstatic.com\" crossorigin>
        <link href=\"https://fonts.googleapis.com/css2?family=Lato:ital,wght@0,100;0,300;0,400;0,700;0,900;1,100;1,300;1,400;1,700;1,900&display=swap\" rel=\"stylesheet\">

        <title>{{ name }}</title>
    </head>
{% endblock %}



{% block header %}
    <div class=\"container\">
       <header>
           <a href=\"{{ path('root') }}\">
               <img class = \"logo\" src=\"{{ asset('build/images/Logo1.svg') }}\" alt=\"\">
           </a>

           <div class=\"nav_bar\">
               {% if is_granted ('IS_AUTHENTICATED_FULLY') %}
                   <a  id = \"login-show\" href=\"/logout\">
                       <div class=\"login_btn\">
                           <p>Logout</p>
                       </div>
                   </a>
               {% elseif not is_granted ('IS_AUTHENTICATED_FULLY') %}
                   <a id = \"login_button\">
                       <div class=\"login_btn\">
                           <p>Login</p>
                       </div>
                   </a>
               {% endif %}

           </div>
       </header>
    </div>
{% endblock %}

{% block body %}
{% endblock %}

{% block footer %}
    <footer>
        <div class=\"container\">
            <div class=\"footer_content\">
                <p>© 2022 Sunflower</p>
            </div>
        </div>
    </footer>
{% endblock %}
    {% block stylesheets%}
        {{ encore_entry_link_tags('root') }}
    {% endblock%}

    {% block javascripts%}

        {{ encore_entry_script_tags('root') }}
    {% endblock%}
", "/base.html.twig", "/var/www/sunflower/templates/base.html.twig");
