    public function getSourceContext()
    {
        return new Source("{% extends 'root/index.html.twig' %}

{% block header %}
    <link href=\"{{ asset('css/root.css') }}\" rel=\"stylesheet\"/>
    <link href=\"{{ asset('css/userForm.css') }}\" rel=\"stylesheet\"/>
{% endblock %}

{% block body %}
    <body>
    <div class=\"container\">
        <div class=\"container_inner\">
            <p class = \"page_header\">Login</p>
            <div class=\"form\">
                {% if error %}
                    <div>{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                {% endif %}

                <form action=\"{{ path('login') }}\" method=\"post\">
                    <div class=\"form_row\">
                        <label class = \"form_string\" for=\"username\">Username:</label>
                        <input type=\"text\" id=\"username\" name=\"_username\" value=\"{{ last_username }}\"/>
                    </div>

                    <div class=\"form_row\">
                        <label for=\"password\">Password:</label>
                        <input type=\"password\" id=\"password\" name=\"_password\"/>
                    </div>

                    <button type=\"submit\">login</button>
                </form>
            </div>
        </div>
    </div>
    </body>
{% endblock %}



", "login/index.html.twig", "/var/www/sunflower/templates/login/index.html.twig");
